import React, { useState, useEffect, useRef } from "react";
import { useToggle } from "./useToggle";

export default function Button() {
  //USE STATE
  const [buttonText, setButtonText] = useState("click me please");
  const [count, setCount] = useState(0);
  function incrementCount() {
    setCount(count + 1);
  }
  function decrementCount() {
    setCount(count - 1);
  }

  //USE REF
  const inputEl = useRef(null);
  const intervalRef = useRef();

  //USE EFFECT
  useEffect(() => {
    const id = setInterval(() => {
      console.log("useEffect");
    }, 3000);

    intervalRef.current = id;

    return () => clearInterval(intervalRef.current);
  });

  //USE TOGGLE
  const [open, toggle] = useToggle(false);

  return (
    <div>
      <h1>useState</h1>
      <span>rerender component without refresh page</span>
      <div className="flex">
        <button onClick={() => setButtonText("clicked")}>{buttonText}</button>
      </div>
      <div style={{display:"flex", justifyContent:"space-around"}}>
        <button onClick={decrementCount}>-</button>
        <h1>{count}</h1>
        <button onClick={incrementCount}>+</button>
      </div>

      <h1>useRef</h1>
      <span>for "behind-the-scenes" stuff (i.e. updating variables) without re-rendering component</span>
      <div className="flex">
        <input ref={inputEl} />
        <button onClick={() => inputEl.current.focus()}>Focus the input</button>
      </div>

      <h1>useEffect</h1>
      <span>(open console)</span>

      <h1>useToggle</h1>
      <span>similar to v-if in Vue</span><br />
      {open && <input />}
      <button onClick={toggle}>Toggle</button>
    </div>
  );
}
